package edu.kit.ipd.sdq.vitruvius.framework.mir.testframework.tests;

import edu.kit.ipd.sdq.vitruvius.framework.contracts.interfaces.Change2CommandTransforming;
import edu.kit.ipd.sdq.vitruvius.framework.mir.generator.MIRIntermediateLanguageGenerator;
import edu.kit.ipd.sdq.vitruvius.tests.VitruviusEMFCasestudyTest;

/**
 * Abstact test class for a mapping generated by MIR.
 * @author Dominik Werle
 */
public abstract class AbstractMIRTestBase extends VitruviusEMFCasestudyTest {
	/**
	 * Returns the content of the MIR file to create the {@link Change2CommandTransforming} from.
	 * @return the content of the MIR file to create the {@link Change2CommandTransforming} from
	 */
	protected abstract String getMIRFileContent();
	
	protected abstract String getChange2CommandTransformerPackage();
	protected String getChange2CommandTransformerClassName() {
		return MIRIntermediateLanguageGenerator.DEFAULT_CLASS_NAME;
	}
}
